<?php

/***************************************************************
* 
* PayPal-Button-Plugin für moziloCMS
* Gibt einen PayPal-Button aus.
* 
***************************************************************/
class moziloPayPal extends Plugin {


    /***************************************************************
    * 
    * Gibt den HTML-Code zurück, mit dem die Plugin-Variable ersetzt 
    * wird.
    * 
    ***************************************************************/
    function getContent($value) {
        global $URL_BASE;
        global $PLUGIN_DIR_NAME;

        $paypal = '<form action="https://www.paypal.com/cgi-bin/webscr" method="post" title="Unterst&uuml;tzen Sie mozilo mit einer Spende!">
                <input type="hidden" name="cmd" value="_s-xclick" />
                <input type="image" src="'.$URL_BASE.$PLUGIN_DIR_NAME.'/moziloPayPal/spendenbanner105x40.png" name="submit" alt="Zahlen Sie mit PayPal - schnell, kostenlos und sicher!" />
                <img alt="Unterst&uuml;tzen Sie mozilo mit einer Spende" src="https://www.paypal.com/de_DE/i/scr/pixel.gif" width="1" height="1" />
                <input type="hidden" name="encrypted" value="-----BEGIN PKCS7----------END PKCS7-----" />
                </form>';
        if($this->settings->get("paypalbutton")) {
            $paypal = str_replace('{PAYPALBUTTON}',$paypal,$this->settings->get("paypalbutton"));
        }
        return $paypal;
    } // function getContent
    
    
    
    /***************************************************************
    * 
    * Gibt die Konfigurationsoptionen als Array zurück.
    * 
    ***************************************************************/
    function getConfig() {
        global $ADMIN_CONF;
        $language = $ADMIN_CONF->get("language");

        // Rückgabe-Array initialisieren
        // Das muß auf jeden Fall geschehen!
        $config['deDE'] = array();
        $config['enEN'] = array();
        $config['deDE']['paypalbutton'] = array(
            "type" => "textarea",                       // Pflicht:  Eingabetyp 
            "cols" => "50",                             // Pflicht:  Spaltenanzahl 
            "rows" => "7",                              // Pflicht:  Zeilenanzahl
            "description" => "Hier können sie um denn {PAYPALBUTTON} HTML Code erzeugen",     // Pflicht:  Beschreibung
        );
        // Nicht vergessen: Das gesamte Array zurückgeben
        if(isset($config[$language])) {
            return $config[$language];
        } else {
            return $config['deDE'];
        }
    } // function getConfig    
    
    
    /***************************************************************
    * 
    * Gibt die Plugin-Infos als Array zurück.
    * 
    ***************************************************************/
    function getInfo() {
        global $ADMIN_CONF;
        $language = $ADMIN_CONF->get("language");

        $info['deDE'] = array(
            // Plugin-Name
            "<b>PayPal-Button</b> 0.1",
            // CMS-Version
            "1.12",
            // Kurzbeschreibung
            'Erzeugt den PayPal-Spendenbutton. <br />
            <br />
            <span style="font-weight:bold;">Nutzung:</span><br />
            {moziloPayPal} erzeugt den PayPal-Button.<br /><br />
            <span style="font-weight:bold;">Mit HTML:</span><br />
            Unten im Textfeld z.B. <span>&#x003C;</span>h1<span>&#x003E;</span>Spenden<span>&#x003C;/</span>h1<span>&#x003E;</span>{PAYPALBUTTON}',
            // Name des Autors
            "mozilo",
            // Download-URL
            "http://cms.mozilo.de",
            array('{moziloPayPal}' => 'PayPal-Button')
            );
        if(isset($info[$language])) {
            return $info[$language];
        } else {
            return $info['deDE'];
        }
    } // function getInfo

} // class DEMOPLUGIN

?>